import React from 'react';
import './App.css';

class Calculator extends React.Component{
  constructor(props){
    super(props)
    this.state ={
      current:'',
      previous: ''
    }
    this.appendNumber = this.appendNumber.bind(this)
    this.clear= this.clear.bind(this)
    this.update = this.update.bind(this)
  };

//  update=(operator)=>{
//    this.setState({
//     previous: `${this.state.current}` + `${operator.target.innerText}`
//    })
//  }

  delete(){

  }
  operation(){

  }

 appendNumber (number){
    const text = number.target.innerText;
   if(text === '.' && this.state.current.includes('.')) return 
   if (text === 'AC' ) return this.clear()
   if (text === '+' || '-' || '*' || '+') return this.update()
    this.setState({  
      current: `${this.state.current}` + `${number.target.innerText}`
    })
    console.log(number.target.innerText)
  };

  clear = () =>{
    this.setState({
      current:'',
      previous: ''
    })
}

  render(){

  return (
    <>
      <div className='calculator'>
          <div className='buttons'>
              <div id='display'>
                <div className='previous'>{this.state.previous}</div>
                <div className='current'>{this.state.current}</div>
              </div>
              <div className='btns' onClick={this.appendNumber} >
              <button id='equals'>=</button>
                  <button id='zero'>0</button>
                  <button id='one'>1</button>
                  <button id='two'>2</button>
                  <button id='three'>3</button>
                  <button id='four'>4</button>
                  <button id='five'>5</button>
                  <button id='six'>6</button>
                  <button id='seven'>7</button>
                  <button id='eight'>8</button>
                  <button id='nine'>9</button>
              <button id='add'>+</button>
              <button id='substract'>-</button>
              <button id='multiply'>*</button>
              <button id='divide'>รท</button>
              <button id='decimal'>.</button>
              <button id='clear' onClick={this.clear}>AC</button>
              </div>
          </div>

      </div>
    </>
  );
}
}

export default Calculator;
