import React from 'react';
import './App.css';

class Calculator extends React.Component{
  constructor(props){
    super(props)
    this.state ={
      current:'',
      previous:'',
      display:0
    }
    this.operations = this.operations.bind(this)
    this.clear= this.clear.bind(this)
  };

 operations(number){
   const text = number.target.innerText;
   const {current} =this.state;
   const {previous} = this.state;
   if(text === '.' && this.state.current.includes('.')) return 
   if (text === 'AC' ) return this.clear()
   if( text === '=') return (
   this.setState({
     previous: `${eval(`${previous}` + `${current}`)}`,
     current: '',
     display: eval(`${previous}` + `${current}`),
   }) );

  const regex = /[+*/]/
   if (text === '+' || text === '*'|| text === '/' )
    return (

      this.setState({ 
        previous: `${previous}`.slice(0,previous.length+2 - previous.split('').reverse().findIndex(char => regex.test(char) === false)) + `${current}` + `${number.target.innerText}`, 
        display: 0,
        current: ''}))
        console.log(previous.split('').reverse().findIndex(char => regex.test(char) !== true  ))

    if(text === '-') return(
      previous.charAt(previous.length -1) !== text ? 
      this.setState({ 
        previous: `${previous}` + `${current}` + `${number.target.innerText}`, 
        display: 0,
        current: ''}) 
        : this.setState({ 
          previous: `${previous}` + `${current}` + ` ${number.target.innerText}`, 
          display: 0,
          current: ''}) )


    if (text === '0') return(
      current.charAt(current.length -1) !== text ? 
      this.setState({  
        current: `${this.state.current}` + `${number.target.innerText}`,
        display: `${this.state.current}` + `${number.target.innerText}`,
       })
       : this.setState({
        current: `${this.state.current}`.slice(0,-1) + `${number.target.innerText}`,
        display: `${this.state.current}`.slice(0,-1) + `${number.target.innerText}`,
       })
    )

    this.setState({  
     current: `${this.state.current}` + `${number.target.innerText}`,
     display: `${this.state.current}` + `${number.target.innerText}`,
    })
  };

  clear = () =>{
    this.setState({
      current:'',
      previous: '',
      display: 0
    })
}



  render(){
  return (
    <>
      <div className='calculator'>
          <div className='buttons'>
              <div id='displays'>
                <div className='previous'>{this.state.previous}</div>
                <div id="display">{this.state.display}</div>
              </div>
              <div className='btns' onClick={this.operations} >
              <button id='equals'>=</button>
                  <button id='zero'>0</button>
                  <button id='one'>1</button>
                  <button id='two'>2</button>
                  <button id='three'>3</button>
                  <button id='four'>4</button>
                  <button id='five'>5</button>
                  <button id='six'>6</button>
                  <button id='seven'>7</button>
                  <button id='eight'>8</button>
                  <button id='nine'>9</button>
              <button id='add'>+</button>
              <button id='subtract'>-</button>
              <button id='multiply'>*</button>
              <button id='divide'>/</button>
              <button id='decimal'>.</button>
              <button id='clear'>AC</button>
              </div>
          </div>

      </div>
    </>
  );
}
}

export default Calculator;
