import React from 'react';
import './App.css';

class Calculator extends React.Component{
  constructor(props){
    super(props)
    this.state ={
      current:'',
      previous: ''
    }
    this.appendNumber = this.appendNumber.bind(this)
    this.clear= this.clear(this)
  };



  delete(){

  }
  operation(){

  }

 appendNumber (number){
   if(number === '.' && this.state.current.includes('.')) return 
    this.setState({  
      current: `${this.state.current}` + `${number.target.innerText}`
    })
    console.log(number.target.innerText)
  };

  clear = () =>{
    this.setState({
      current:'',
      previous: ''
    })
}

  render(){





  return (
    <>
      <div className='calculator'>
          <div className='buttons'>
              <div id='display'>
                <div className='previous'>4</div>
                <div className='current'>{this.state.current}</div>
              </div>
              <div className='btns' >
              <button id='equals'>=</button>
              <div className='numbers' onClick={this.appendNumber}>
                  <button id='zero'>0</button>
                  <button id='one'>1</button>
                  <button id='two'>2</button>
                  <button id='three'>3</button>
                  <button id='four'>4</button>
                  <button id='five'>5</button>
                  <button id='six'>6</button>
                  <button id='seven'>7</button>
                  <button id='eight'>8</button>
                  <button id='nine'>9</button>
              </div>
              <button id='add'>+</button>
              <button id='substract'>-</button>
              <button id='multiply'>*</button>
              <button id='divide'>รท</button>
              <button id='decimal'>.</button>
              <button id='clear' onClick={this.clear}>AC</button>
              </div>
          </div>

      </div>
    </>
  );
}
}

export default Calculator;
